import React from 'react';

export const List = ({ todoItems, setTodoItems }) => {
	const handleDeleteItem = (event) => {
		console.log(`The order of item to be delted in state is: ${event.target.getAttribute("order")}`);
		console.log("please delete me!");

		const orderOfItemToBeDeleted = event.target.getAttribute("order");
		const newTodoItems = todoItems.filter((_, index) => index !== orderOfItemToBeDeleted);
		setTodoItems(newTodoItems);
	};

	const list = todoItems.map((element, index) => 
		<li key={index}>
			{element}
			<button order={index} onClick={handleDeleteItem}>x</button>
		</li>);

	return <ul>{list}</ul>;
};

