import React from 'react';

export const Input = (todoItems, setTodoItems) => {
	const handleKeyDown = (event) => {
		if(event.key === "Enter") {
			event.preventDefault();
			console.log(`before: ${todoItems}`);
			setTodoItems()

			event.target.value = '';
		}
	};

	return <input type="text" onKeyDown={handleKeyDown}/>
};
